import express from 'express';
const app = express()
const port = 3000

app.get('/home', (req, res) => {
    res.send('Hello World!')
})
app.get('/list', (rep,res) => {
    res.sendFile(path.join(__dirname, '.index.html'));
})

app.listen(port, () => {
    console.log(`Example app listening on port ${port}`)
})

